var form=document.getElementById("form"),username=document.getElementById("username"),email=document.getElementById("email"),password=document.getElementById("password"),password2=document.getElementById("password2");function showError(e,s){const r=e.parentElement;r.className="form-control error";r.querySelector("small").innerText=s}function showSucess(e){e.parentElement.className="form-control success"}function checkEmail(e){/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e.value.trim())?showSucess(e):showError(e,"Email is not valid")}function checkRequired(e){e.forEach((function(e){""===e.value.trim()?showError(e,getFieldName(e)+" is required"):showSucess(username)}))}function matchPassword(e,s){e.value!==s.value?showError(s,"Password do not match"):showSucess(s)}function getFieldName(e){return e.id.charAt(0).toUpperCase()+e.id.slice(1)}function checkLength(e,s,r){e.value.length<s?showError(e,`${getFieldName(e)} should be atleast ${s} characters`):e.value.length>r?showError(e,`${getFieldName(e)} should be less than ${r} characters`):showSucess(e)}form.addEventListener("submit",(function(e){e.preventDefault(),checkRequired([username,email,password,password2]),checkLength(username,3,20),checkLength(password,4,20),checkEmail(email),matchPassword(password,password2)}));